srcDoc.css:
#root {
  position: relative;
}

.title_box {
  position: absolute;
  top: 80px;
  left: 180px;
}

.title {
  display: block;
  background-color: red;
  color: white;
  width: 120px;
  text-align: center;
  font-family: Arial, Helvetica, sans-serif;
}

.title_h1 {
  display: block;
  width: 400px;
  font-size: 3rem;
  font-family: Arial, Helvetica, sans-serif;
  margin: 0;
}

.title_span {
  display: block;
  font-size: 1.5em;
  margin: 10px 0;
  font-family: Arial, Helvetica, sans-serif;
}

.title_btn {
  display: flex;
  justify-content: center;
  align-items: center;
  width: 150px;
  height: 50px;
  border-radius: 25px;
  border: 1px #751FFF;
  background-color: #751FFF;
}

.title_img {
  width: 100%;
  height: 100%;
}

